        <!-- Viaje Header -->
        <div class="viaje__header">

            <!-- Viaje Image -->
            <figure class="viaje__image">
            <?php
                    if( has_post_thumbnail() ) {
                        echo genesis_get_image( array(
                            'size'     => 'rv-featured',
                            'itemprop' => 'image',
                            'attr' => array(
                                'class'    => 'viaje__image__img',
                            ),
                        ) ); 
                    }else { ?>
                        <img class="viaje__image__img" src="<?php echo get_stylesheet_directory_uri() ?>/images/fallback.png" alt="Pais imagen">
                    <?php } ?>
                <div class="viaje__image__gradient"></div>
            </figure> <!-- End viaje__image -->

            <!-- Viaje Info -->
            <div class="viaje__header__info">
            <?php 
                $terms = get_the_terms( $post->ID, 'destinos' );
                if ( $terms && ! is_wp_error( $terms ) ) { 
                    // Inicializar la variable antes de usarla
                    $entry_terms = '';

                    ?>
                    <div class="viaje__pais">
                        <span class="viaje__guion">—</span>
                        <span class="viaje__paises">
                        <?php 
                        foreach ( $terms as $term ) {
                            $entry_terms .= $term->name . ', ';
                        } 
                        // Eliminar la última coma y espacio
                        $entry_terms = rtrim( $entry_terms, ', ' );
                        echo esc_html( $entry_terms ); // Escapar para mayor seguridad
                        ?>
                        <span class="viaje__guion">—</span>
                        </span>
                    </div>
                <?php 
                } 
                ?>
            
                <h2 class="viaje__title" itemprop="headline">
                    <?php
                    $alt_title = get_field('rv_alt_title', $post->ID);
                    $alt_title_second_line = get_field('rv_alt_title_second_line', $post->ID);
                    if( $alt_title && $alt_title_second_line ) { ?>
                        <span><?php echo $alt_title ?></span><span><?php echo $alt_title_second_line ?></span>
                    <?php }elseif( $alt_title ){ ?>
                        <?php echo $alt_title ?>
                    <?php }else{ ?>
                        <?php the_title() ?>
                    <?php } ?>
                </h2>
            </div><!-- End viaje__header__info -->

        </div><!-- End viaje__header -->

